({ $data, $params, $dict, $theme, $files }) => {
const chartData = $data.operating_metrics;

const totalPerModule = chartData.xAxis.map((_, idx) => 
  chartData.series.reduce((sum, s) => sum + s.data[idx], 0)
);

const series = chartData.series.map(item => ({
  name: item.name,
  type: 'bar',
  stack: 'total', 
    barWidth: 30, 

  label: {
    show: true,
    position: 'insideRight',    
    fontSize: 8,
    color: '#fff',
    formatter: (params) => {
      const idx = params.dataIndex;
      const value = params.value;
      const total = totalPerModule[idx];
      const percentage =  ((value / total) * 100);
      return percentage >= 4.0 ? percentage.toFixed(1) + '%' :  percentage.toFixed(0) + '%';

    }
  },

  data: item.data
}));

const option = {
    graphic: [{
        type: 'text',
        left: 20, 
        top: 50, 
        style: {
            text: 'Platform Module',
            fontSize: 14,
            fontWeight: 'bold',
        }
    },  {
      type: 'text',
      left: 20,
      bottom: 30,
     style: {
            text: 'Client Type',
            fontSize: 14,
            fontWeight: 'bold',
        }
    }],
  legend: [ 
    {
        data: chartData.series.map(s => s.name),
        bottom: -5,
        left: 'center',
        orient: 'horizontal',
        itemWidth: 14,
        itemHeight: 14,
        itemGap: 20,
        textStyle: {
            fontSize: 12,
        }
    },
    {
        data: [], 
        bottom: 30,
        left: 'center',
        textStyle: {
            fontSize: 12,
            fontWeight: 'bold'
        },
        formatter: () => 'Client Type' 
    }
],

  grid: { left: '3%', right: '4%', bottom: '12%', containLabel: true },
  xAxis: {
    type: 'value',
    axisLine: { show: false }, 
    splitLine: { show: true },
  },
  yAxis: {
    type: 'category',
    data: chartData.xAxis,
    axisLine: { show: true },

  },
  series: series
};

return option;

}

({ $data, $params, $dict, $theme, $files }) => {
const reportData = $data.executive_summary.financial_highlights;

const metrics = ["Revenue", "Gross Profit", "EBITDA"];
const data2024 = [];
const dataDelta = [];
const percentChange = [];

metrics.forEach((metric) => {
  const key = metric.replace(" ", "_").toLowerCase();
  const d = reportData[key];

  const v2024 = d["2024"] / 1_000_000; 
  const deltaV = d.growth_absolute / 1_000_000;
  const deltaP = d.growth_percentage;

  data2024.push({
    value: v2024,
    itemStyle: { borderRadius: [0, 0, 0, 0] },
  });

  dataDelta.push({
    value: deltaV,
    itemStyle: { borderRadius: [8, 8, 0, 0] },
  });

  percentChange.push(`+${deltaP}%`);
});

const option = {
  title: { show: false },
  tooltip: {
    trigger: "axis",
    axisPointer: { type: "shadow" },
    formatter: function (params) {
      if (!params?.length) return "";

      const base = params[0].data.value;
      const delta = params[1].data.value;
      const total = base + delta;
      const metricName = params[0].name;

      const data = reportData[metricName.replace(" ", "_").toLowerCase()];
      const deltaP = data.growth_percentage;

      return `
        ${metricName}<br/>
        2025 Total: <strong>${total.toFixed(1)}€M</strong><br/>
        2024 Base: ${base.toFixed(1)}€M<br/>
        Growth: ${delta.toFixed(1)}€M (${deltaP.toFixed(1)}%)
      `;
    },
  },
  legend: {
    show: true,
    top: "10px",
    right: "10px",
  },
  grid: {
    left: "3%",
    right: "4%",
    bottom: "10%",
    containLabel: true,
  },
  graphic: [
    {
      type: "text",
      top: "50%",
      left: 5,
      style: { text: "€ Millions", fontFamily: 'Roboto'},
      rotation: Math.PI / 2,
      z: 1,
    },
  ],
  xAxis: {
    type: "category",
    data: metrics,
    axisLine: { lineStyle: { color: "#e5e7eb" } },
    axisLabel: {
      formatter: function (value, index) {
        const percentage = percentChange[index];
        return `{a|${value}}\n{b|${percentage}}`;
      },
      rich: {
        a: { color: "#525252", lineHeight: 20 },
        b: { color: "#581c87", fontWeight: "bold", fontSize: 14, lineHeight: 26},
      },
    },
    axisTick: { show: false },
  },
  yAxis: {
    type: "value",
    name: "",
    axisLine: { show: true, lineStyle: { color: "#e5e7eb" } },
    splitLine: { lineStyle: { color: "#f3f4f6" } },
  },
  series: [
    {
      name: "2024",
      type: "bar",
      stack: "total",
      data: data2024,
      label: {
        show: true,
        position: "inside",
        formatter: (p) => p.value.toFixed(0),
      },
      barWidth: "50%",
    },
    {
      name: "2025",
      type: "bar",
      stack: "total",
      data: dataDelta,
      label: {
        show: true,
        position: "top",
        formatter: (p) => {
          const total = data2024[p.dataIndex].value + p.value;
          return total.toFixed(0);
        },
      },
      barWidth: "50%",
    },
  ],
};

return option;

}

