({ $data, $params, $dict }) => {
let data = $data.outlook;

const prev = data[0];
const curr = data[1];

// Calculate growth rates
const arrGrowthRate = (curr.ARR - prev.ARR) / prev.ARR;
const ebitdaGrowthRate = (curr['EBITDA Margin'] - prev['EBITDA Margin']) / prev['EBITDA Margin'];

// Project 2026 values using same growth
const arr = curr.ARR * (1 + arrGrowthRate);
const ebitda = curr['EBITDA Margin'] * (1 + ebitdaGrowthRate);

return {
  arr,
  ebitda
};
}

