($params, $data) => {
 let cats = {};
  for (let t of ($data?.statement?.transactions ?? [])) {
    if (t.debit) 
    	cats[t.category] = (cats[t.category] ?? 0) + t.debit;
  }
  let result = Object.keys(cats).map(k => ({ category: k, amount: cats[k] }));
  result.sort((a, b) => a.amount - b.amount);
  return result;  
}
