/* .elecrticity-chart .cxb-svg .cxb-bar rect.cxe-bar-rect:first-of-type{
    fill: blue;
} */

.electricity-chart .cxb-svg .cxb-bar:last-of-type rect.cxe-bar-rect{
    fill: var(--light-green);
}

.electricity-chart .cxb-svg .cxb-bar:nth-last-of-type(2) rect.cxe-bar-rect{
    fill: var(--light-blue);
}

.electricity-chart .cxb-svg .cxb-bar:nth-last-of-type(3) rect.cxe-bar-rect{
    fill: var(--dark-gray);
}

.electricity-chart .cxb-svg .cxb-categoryaxis text.cxe-categoryaxis-label:nth-child(2) {
    font-weight: bold;
}
